import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import '../Css/header.css';


const Header = () => {

    const handleDownload = () => {
        const link = document.createElement('a');
        link.href = '../Assets/CV_MUHAMMAD_ALVIN_NEW.pdf'; // Ganti dengan path ke file PDF yang ingin didownload
        link.setAttribute('download', 'file.pdf'); // Nama file yang akan di-download
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

  return (
    <div className="header">
        <Container>
        <Row lg={2}>
            <Col>
                <p className='fwb'>Halo, Perkenalkan Saya,</p>
                <h1 className='fwb text-yellow'>MUHAMMAD ALVIN</h1>
                <p className='fwb'>Junior Frontend Developer</p>
                <button onClick={handleDownload}>
                    Download PDF
                </button>
            </Col>
        </Row>
        </Container>
    </div>
  );
}

export default Header;
